import React, { useEffect, useState } from "react";


const Posts = () => {

  const [items, setPos] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/gayItem')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch posts');
        }
        return response.json();
      })
      .then(data => {
        setPosts(data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  return (
    <div>
        <div class="container">
          <header class="d-flex flex-wrap align-items-center justify-content-center justify-content-md-between py-3 mb-4 border-bottom">
            <div class="col-md-3 mb-2 mb-md-0">
              <a href="/" class="d-inline-flex link-body-emphasis text-decoration-none">
                <svg class="bi" width="40" height="32" role="img" aria-label="Bootstrap"></svg>
              </a>
            </div>

            <ul class="nav col-12 col-md-auto mb-2 justify-content-center mb-md-0">
              <li><a href="/" class="nav-link px-2 link-secondary">Home</a></li>
              <li><a href="#" class="nav-link px-2 link-secondary">Post Feed</a></li>
              <li><a href="/collections" class="nav-link px-2 link-secondary">Collections</a></li>
            </ul>

            <div class="col-md-3 text-end">
              <button type="button" class="btn btn-outline-primary me-2">Login</button>
              <button type="button" class="btn btn-primary">Sign-up</button>
            </div>
          </header>
        </div>  

            <div className="box">
          <h2>Post Feed:</h2>
          {items.map(item => (
            <div key={item.id}>
              <p>{item.postDate}</p>
              <p>{item.postBody}</p>
              <p>{item.postImg}</p>
            </div>
          ))}
        </div>

    </div>
  )
}

export default Posts
