import React, { useState, useEffect } from 'react';

function Update() {
  const [itemId, setItemId] = useState('');
  const [item, setItem] = useState({
    postDate: '',   
    postBody: '',
    postImg: '',
  });

  useEffect(() => {
    // Fetch the item data based on itemId
    if (itemId) { 
      fetch(`http://localhost:3666/post/${itemId}`, {
        method: 'GET',
      })
        .then(response => response.json())
        .then(data => {
          setItem(data);
        })
        .catch(error => {
          console.error('Error fetching item data:', error);
        });
    }
  }, [itemId]);

  const handleInputChange = e => {
    const { name, value } = e.target;
    setItem(prevItem => ({
      ...prevItem,
      [name]: value,
    }));
  };

  const handleUpdate = e => {
    e.preventDefault();
    fetch(`http://localhost:3666/post/update/${itemId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(item),
    })
      .then(response => response.json())
      .then(updatedData => {
        console.log('Item updated successfully:', updatedData);
      })
      .catch(error => {
        console.error('Error updating item:', error);
      });
  };

  return (
    <div className='Row'>
      <h2>Update Item</h2>
      {item ? (
        <form onSubmit={handleUpdate}>
          <label>
            Date:
            <input type="text" name="postDate" value={item.postDate} onChange={handleInputChange}/>
          </label>
          <br />
          <label>
            Post:
            <input type="text" name="postBody" value={item.postBody} onChange={handleInputChange}/>
          </label>
          <br />
          <label>
            Img:
            <input
              type="text" name="postImg" value={item.postImg} onChange={handleInputChange}/>
          </label>
          <br />
          <button type="submit">Update Item</button>
        </form>
      ) : (
        <p>Loading item data...</p>
      )}
    </div>
  );
}

export default Update;
