import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function Update() {
  const { itemId } = useParams(); 
  const [item, setItem] = useState(null); 

  useEffect(() => {
    fetch(`http://localhost:3666/post/${itemId}`)
      .then(response => response.json())
      .then(data => {
        setItem(data);
        setUpdatedItem({
          postDate: data.postDate, // Set the pre-existing data
          postBody: data.postBody,
          postImg: data.postImg
        });
      })
      .catch(error => {
        console.error('Error fetching item data:', error);
      });
  }, [itemId]);

  const handleInputChange = (e) => {
    setUpdatedItem({ ...updatedItem, [e.target.name]: e.target.value });
  };

  const handleUpdate = (e) => {
    e.preventDefault();
    fetch(`http://localhost:3666/post/update/${itemId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedItem),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to update item');
        }
        console.log('Item updated successfully');
        setUpdatedItem({
          postDate: '',
          postBody: '',
          postImg: ''
        });
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div className='Row'>
      <h2>Update Item</h2>
      {item ? (
        <form onSubmit={handleUpdate}>
          <label>
            Date:
            <input type="text" name="postDate" value={updatedItem.postDate} onChange={handleInputChange}/>
          </label>
          <br />
          <label>
            Post:
            <input type="text" name="postBody" value={updatedItem.postBody} onChange={handleInputChange}/>
          </label>
          <br />
          <label>
            Img:
            <input
              type="text" name="postImg" value={updatedItem.postImg} onChange={handleInputChange}/>
          </label>
          <br />
          <button type="submit">Update Item</button>
        </form>
      ) : (
        <p>Loading item data...</p>
      )}
    </div>
  );
}

export default Update;
