import React, { useState, useEffect } from 'react';
import Nav from './Nav';
import { Link } from 'react-router-dom';

function UserProfile() {
  const [user, setUser] = useState(null);
  const [isFriend, setIsFriend] = useState(false);
  const [notebookText, setNotebookText] = useState('');
  const userId = sessionStorage.getItem('userId');
  const isUserLoggedIn = Boolean(userId);

  useEffect(() => {
    console.log('Fetching user data...');
    fetch(`http://localhost:3666/user/${userId}`, {
      method: 'GET',
      credentials: 'include',
    })
      .then(response => response.json())
      .then(data => {
        console.log('User data:', data);
        setUser(data);
        setNotebookText(data.NoteBook || ''); 
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, [userId]);

  const handleNotebookChange = (event) => {
    setNotebookText(event.target.value);
  };

  const handleSaveNotebook = async () => {
    try {
      const response = await fetch(`http://localhost:3666/user/${userId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ NoteBook: notebookText }),
      });
      const data = await response.json();
      if (data.success) {
        console.log('Notebook saved successfully');
      } else {
        console.error(data.error);
      }
    } catch (error) {
      console.error(error);
    }
  };


  return (
    <div>
      <div>
        <Nav/>
      </div>
      <div className='Profile'>
        {user ? (
          <div>
            <h1>User Profile</h1>
            <p>Email: {user.Gmail}</p>
            <p>Username: {user.UserName}</p>
            <p>Bio: {user.Bio}</p>
            <p>Name: {user.Name}</p>
            <p>Img: {user.Img}</p>
            {isUserLoggedIn && userId !== user._id && (
              <div>
              <textarea
                value={notebookText}
                onChange={handleNotebookChange}
                placeholder="Write your notes here..."
              />
              <button onClick={handleSaveNotebook}>Save Notebook</button>
            </div>
            )}
            <Link to='/updateUser'>
              <button>Edit Profile</button>
            </Link>
          </div>
        ) : (
          <p>Loading user data...</p>
        )}
      </div>
    </div>
  );
}

export default UserProfile;
