import React, { useState, useEffect } from 'react';

function UpdateUser() {
  const [user, setUser] = useState(null);
  const [updatedUser, setUpdatedUser] = useState({
    Bio: '',
    UserName: '',
    Img: '',
    Name: '',
  });

  useEffect(() => {
    const userId = sessionStorage.getItem('userId');
    fetch(`http://localhost:3666/user/${userId}`, {
      method: 'GET',
      credentials: 'include',
    })
      .then(response => response.json())
      .then(data => {
        setUser(data);
        setUpdatedUser(data); 
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, []);

  const handleInputChange = e => {
    const { name, value } = e.target;
    setUpdatedUser(prevUser => ({
      ...prevUser,
      [name]: value,
    }));
  };

  const handleUpdateClick = () => {
    const userId = sessionStorage.getItem('userId');
    fetch(`http://localhost:3666/user/${userId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedUser),
      credentials: 'include',
    })
      .then(response => response.json())
      .then(data => {
        console.log('Updated user data:', data);
        setUser(data);
      })
      .catch(error => {
        console.error('Error updating user data:', error);
      });
  };

  return (
    <div className='Row'>
      {user ? (
        <div>
          <h2>Update User</h2>
          <label>
          <input type='text' name='UserName' value={updatedUser.UserName} onChange={handleInputChange}/>
          </label>
          <label>
          <input type='text' name='Bio' value={updatedUser.Bio} onChange={handleInputChange}/>
          </label>
          <label>
          <input
            type='text'
            name='Img'
            value={updatedUser.Img}
            onChange={handleInputChange}
          />
          </label>
          <label></label>
          <input
            type='text'
            name='Name'
            value={updatedUser.Name}
            onChange={handleInputChange}
          />
          <button onClick={handleUpdateClick}>Update</button>
        </div>
      ) : (
        <p>Loading user data...</p>
      )}
    </div>
  );
}

export default UpdateUser;
