import React, { useState, useEffect } from 'react';
import Nav from './Nav';

function UserProfile() {
  const [user, setUser] = useState(null);
  const [notebookText, setNotebookText] = useState('');
  const [posts, setPosts] = useState([]);
  const userId = sessionStorage.getItem('userId');
  const isAdmin = sessionStorage.getItem('isAdmin') === 'true';

  useEffect(() => {
    fetch(`http://localhost:3666/user/${userId}`, {
      method: 'GET',
      credentials: 'include',
    })
      .then(response => response.json())
      .then(data => {
        setUser(data);
        setNotebookText(data.NoteBook || ''); 
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
      });
  }, [userId]);

  useEffect(() => {
    fetch(`http://localhost:3666/post`, {
      method: 'GET',
    })
      .then(response => response.json())
      .then(data => {
        setPosts(data);
      })
      .catch(error => {
        console.error('Error fetching posts:', error);
      });
  }, []);

  const handleNotebookChange = (event) => {
    setNotebookText(event.target.value);
  };

  const handleSaveNotebook = async () => {
    try {
      const response = await fetch(`http://localhost:3666/user/${userId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ NoteBook: notebookText }),
      });
      const data = await response.json();
      if (data.success) {
        console.log('Notebook saved successfully');
      } else {
        console.error(data.error);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <div>
        <Nav/>
      </div>
      <div className='Profile'>
        {user ? (
          <div>
            <h1>User Profile</h1>
            <p>Email: {user.Gmail}</p>
            <p>Username: {user.UserName}</p>
            <p>Bio: {user.Bio}</p>
            <p>Name: {user.Name}</p>
            <p>Img: {user.Img}</p>
            
            <div>
              <textarea
                value={notebookText}
                onChange={handleNotebookChange}
                placeholder="Write your notes here..."
              />
              <button onClick={handleSaveNotebook}>Save Notebook</button>
            </div>
            
            {user.isAdmin && posts.length > 0 && (
              <div>
                <h2>Posts</h2>
                {posts.map((item) => (
                  <div key={item.id} className="PostBox">
                    <h3>{item.postDate}</h3>
                    <p>{item.postImg}</p>
                    <p>{item.postBody}</p>
                  </div>
                ))}
              </div>
            )}
            
          </div>
        ) : (
          <p>Loading user data...</p>
        )}
      </div>
    </div>
  );
}

export default UserProfile;
